let burgerBtn=document.querySelector("#burger-button"),mainMenu=document.querySelector("#nav-menu"),canSeeMenu=!1;burgerBtn.addEventListener("click",(()=>{canSeeMenu?(mainMenu.classList.remove("show-menu"),canSeeMenu=!1):(mainMenu.classList.add("show-menu"),canSeeMenu=!0)}));let closeBtn=document.querySelector("#close-button");closeBtn.addEventListener("click",(()=>{mainMenu.classList.remove("show-menu"),canSeeMenu=!1}));